import db from "../../Kanbas/Database";
import { Link, useLocation, Navigate, Route, Routes, useParams } from "react-router-dom";
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from "@fortawesome/free-solid-svg-icons";
import 'bootstrap/dist/css/bootstrap.min.css';
import CourseNavigation from "./CourseNavigation";
import './style.css'


function Courses() {
  const { courseId } = useParams();
  const course = db.courses.find((course) => course._id === courseId);
  const barsStyle = {
    'font-size': '30px'
  }
  const breadcrumbStyle = {
    'bs-breadcrumb-divider': '>'
  }
  return (
    <>
      <div className="page-header">

        <FontAwesomeIcon icon={faBars} className="red-icon ms-1 me-4" style={barsStyle} />
        <nav aria-label="breadcrumb" style={breadcrumbStyle} className="mt-3">
          <ol class="breadcrumb">
            <li className="breadcrumb-item">
              <Link key={course._id} to={`/Kanbas/Courses/${course._id}`} className="text-red">
                {course.name}
              </Link>
            </li>

          </ol>
        </nav>
      </div >
      <div className="d-flex flex-row">
        <CourseNavigation />
        <div className="page-body">
          <Routes>

            <Route path="/" element={<Navigate to="Home" />} />
            <Route path="Home" element={<h1>Home</h1>} />
            <Route path="Modules" element={<h1>Modules</h1>} />
            <Route path="Assignments" element={<h1>Assignments</h1>} />
            <Route
              path="Assignments/:assignmentId"
              element={<h1>Assignment Editor</h1>}
            />
            <Route path="Grades" element={<h1>Grades</h1>} />
          </Routes>
        </div>
      </div>
    </>
  );
}
export default Courses;
