import { Link, useLocation } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleUser, faGauge, faBook, faCalendarDays, faInbox, faClock, faTv, faQuestionCircle } from "@fortawesome/free-solid-svg-icons";
import './style.css'
function KanbasNavigation() {
  const links = ["Account", "Dashboard", "Courses", "Calendar"];
  const linkIcons = [faCircleUser, faGauge, faBook, faCircleUser, faCalendarDays, faInbox, faClock, faTv, faTv, faQuestionCircle]
  linkIcons.forEach(icon => {
    library.add(icon);
  })
  const { pathname } = useLocation();

  return (
    <div className="sidebar">
      {links.map((item, index) => (
        <Link
          key={index}
          to={`/Kanbas/${item}`}
          className={`sidebar-item ${pathname.includes(item) && "sidebar-item-active"}`}>
          <div class="sidebar-item-icon">
            <FontAwesomeIcon icon="book" />
          </div>
          <div class="sidebar-item-text">{item.title}</div>
        </Link>
      ))}
    </div>
  );
}
export default KanbasNavigation;
