import React from "react";
import { Link, useParams } from "react-router-dom";
import Database from "../../Database";
import './style.css'

export default function Assignments() {
  const { courseId } = useParams();
  const assignments = Database.assignments;
  const courseAssignments = assignments.filter(
    (assignment) => assignment.course === courseId);
  console.log(courseAssignments);
  return (
    <>
      <h1>New Assignments</h1>
      <ul className="list-group">
        <li className="list-group-item d-flex flex-row align-items-center border-0 gray-background">
          Assignments
        </li>
        {courseAssignments.map((assignment, index) => (
          <li className="list-group-item d-flex flex-row align-items-center green-left-border">
            <div className="d-flex flex-column">
              <div className="assignment-title">A1 SETUP</div>
              <div className="assignment-module ">Week 0 - SETUP - Week starting Septmeber 5th (9/15/2022) Module |</div>
              <div className="assignment-due-date"><strong>Due: </strong>Septmeber 18th, 2022 at 11:59pm | 100 points</div>
            </div>
          </li>
        ))}
      </ul>
    </>)
}
